import React, { useState } from 'react';
import axios from 'axios';

const BASE_URL="https://kr-ai-meeting-bot.onrender.com"

const TranscriptInput = () => {
  const [file, setFile] = useState(null);
  const [transcript, setTranscript] = useState('');
  const [summary, setSummary] = useState('');
  const [actionItems, setActionItems] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    setLoading(true);
    try {
      let res;

      //  If file is selected, upload that
      if (file) {
        const formData = new FormData();
        formData.append('file', file);
        res = await axios.post(`${BASE_URL}/transcribe`, formData);
      } else {
        //  If no file, just use backend's saved meeting.txt from Chrome Extension
        res = await axios.post(`${BASE_URL}/transcribe/from-file`);
      }

      setTranscript(res.data.transcript || '');
      setSummary(res.data.summary?.map((s) => s.summary).join('\n') || 'No summary available');
      setActionItems(res.data.action_items || []);
    } catch (err) {
      console.error('Upload failed:', err);
      alert('‚ùå Upload failed. Make sure "meeting.txt" exists in backend /transcripts/ folder or upload a file.');
    } finally {
      setLoading(false);
    }
  };

  const handleClear = () => {
    setFile(null);
    setTranscript('');
    setSummary('');
    setActionItems([]);
  };

  const handleCopy = () => {
    const text = generateExportText();
    navigator.clipboard.writeText(text);
    alert('üìã Copied to clipboard!');
  };

  const handleDownload = () => {
    const text = generateExportText();
    const blob = new Blob([text], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'meeting-summary.txt';
    a.click();
    URL.revokeObjectURL(url);
  };

  const generateMailtoLink = () => {
    const text = generateExportText();
    return `mailto:?subject=Meeting Summary&body=${encodeURIComponent(text)}`;
  };

  const generateExportText = () => {
    let text = 'üìù Meeting Summary:\n';
    text += summary + '\n\n';
    text += '‚úÖ Action Items:\n';
    if (Array.isArray(actionItems)) {
      text += actionItems
        .map((item) => `- ${item.task} [Owner: ${item.owner}] [Deadline: ${item.deadline}]`)
        .join('\n');
    } else {
      text += 'No action items.';
    }
    return text;
  };

  const handleExportToNotion = () => {
    alert('üìì Export to Notion feature coming soon!');
  };

  return (
    <div className="w-full max-w-4xl mx-auto bg-white dark:bg-gray-900 p-6 rounded-lg shadow-lg border dark:border-gray-700">
      <br></br>
      <h2 className="text-lg font-semibold mb-1 text-gray-900 dark:text-white">üìÑ Upload File</h2>

      <div className="mb-4">
        <label className="inline-block bg-gray-100 dark:bg-gray-800 dark:text-white border border-gray-300 dark:border-gray-600 px-4 py-1.5 rounded hover:bg-gray-200 dark:hover:bg-gray-700 cursor-pointer text-center"> {/* ‚úÖ yaha change kiya */}
          {file ? file.name : 'Select a text or audio file'}
          <input
            type="file"
            onChange={handleFileChange}
            className="hidden"
          />
        </label>
      </div>

      <div className="flex gap-4 mb-4">
        <button
          onClick={handleUpload}
          disabled={loading}
          className="border border-teal-600 text-teal-600 px-4 py-1.5 rounded hover:bg-teal-600 hover:text-white transition disabled:opacity-60" // ‚úÖ yaha style same kiya jaise image me
        >
          {loading ? 'Processing...' : 'Generate Summary'}
        </button>

        <button
          onClick={handleClear}
          className="border border-gray-600 text-gray-600 px-4 py-1.5 rounded hover:bg-gray-600 hover:text-white transition" // ‚úÖ yaha bhi same styling ki
        >
          Clear
        </button>
      </div>
      <br></br>
      <div className="flex items-center gap-4 mb-4">
        <button
          onClick={handleUpload}
          disabled={loading}
          className="bg-blue-600 text-white px-4 py-1.5 rounded hover:bg-blue-700 disabled:opacity-60"
        >
          Generate Zoom Meeting Summary
        </button>
      </div>

      {summary && (
        <div className="mb-4">
          <h4 className="font-bold text-gray-900 dark:text-white mb-1">üìù Summary</h4>
          <p className="whitespace-pre-wrap text-gray-800 dark:text-gray-300">{summary}</p>
        </div>
      )}

      {Array.isArray(actionItems) && actionItems.length > 0 && (
        <div className="mb-4">
          <h4 className="font-bold text-gray-900 dark:text-white mb-1">‚úÖ Action Items</h4>
          <ul className="list-disc list-inside text-gray-800 dark:text-gray-300">
            {actionItems.map((item, index) => (
              <li key={index}>
                <strong>Task:</strong> {item.task}<br />
                <strong>Owner:</strong> {item.owner}<br />
                <strong>Deadline:</strong> {item.deadline}
              </li>
            ))}
          </ul>
        </div>
      )}

      {(summary || actionItems.length > 0) && (
        <div className="flex flex-col gap-2">
          <button onClick={handleCopy} className="bg-gray-800 text-white px-4 py-2 rounded hover:bg-gray-900">
            üìã Copy to Clipboard
          </button>
          <button onClick={handleDownload} className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700">
            üíæ Download Summary
          </button>
          <a
            href={generateMailtoLink()}
            className="bg-purple-600 text-white px-4 py-2 rounded text-center hover:bg-purple-700"
          >
            üìß Send via Email
          </a>
          <button onClick={handleExportToNotion} className="bg-yellow-500 text-white px-4 py-2 rounded hover:bg-yellow-600">
            üìì Export to Notion
          </button>
        </div>
      )}
    </div>
  );
};

export default TranscriptInput;
